import React from "react";
const BodyTabla = (props) => {
    eliminarPersona = (indice) => {
        const { personas } = this.state;
    
        this.setState({
          personas: personas.filter((indice, i) => {
            return i !== indice;
          }),
        });
      };
  const filas = props.datosPersonas.map((fila, indice) => {
    return (
      <tr key={indice}>
        <td>{fila.nombre}</td>
        <td>{fila.apellido}</td>
        <td>
          <button onClick={() => props.eliminarPersona(indice)}>
            Eliminar
          </button>
        </td>
      </tr>
    );
  });
  return <tbody>{filas}</tbody>;
};
export default BodyTabla;
